, swap ñ for n, etc  var from = "àáäâèéëêìíïîòóöôùúüûñç·/_,:;";  var to   = "aaaaeeeeiiiioooouuuunc------";  for (var i=0, l=from.length ; i<l ; i++) {    str = str.replace(new RegExp(from.charAt(i), 'g'), to.charAt(i));  }  str = str.replace(/[^a-z0-9 -]/g, '') // remove invalid chars    .replace(/\s+/g, '-') // collapse whitespace and replace by -    .replace(/-+/g, '-'); // collapse dashes  return str;}jQuery('#cf_slug').bind('keyup keypress blur', function()        {            var myStr = jQuery(this).val()            myStr=myStr.toLowerCase();            myStr=myStr.replace(/\s/g , "-");            jQuery('#cf_slug').val(myStr);             slug = jQuery(this).val();             customSlug = slug;                customSlug = string_to_slug(customSlug);                jQuery(this).val(customSlug);                jQuery('.customSlugText').text(customSlug);                newurl = jQuery('#cfslugurl').text();                jQuery('#cfslugurl').attr('href', newurl);                jQuery('#cf_page_url').val('<?php echo get_home_url(); ?>/'+myStr);                jQuery('#customurlError').hide();                jQuery('#customurlError_duplicate').hide();                jQuery('#publish').removeClass('disabledLink');                jQuery('.used_slug').each(function () {                    if (jQuery(this).html() == customSlug) {                         jQuery('#customurlError_duplicate').fadeIn();                         jQuery('#publish').addClass('disabledLink');                    }                });                if ('' == customSlug) {                         jQuery('#customurlError').fadeIn();                         jQuery('#publish').addClass('disabledLink');                    }        });jQuery( '#cf_type' ).change(function() {    if (jQuery(this).val() == 'clickgate') {        jQuery('.cf_url').hide();        jQuery('.clickgateinfo').show();        jQuery('#publish').removeClass('disabledLink');    }    else if (jQuery(this).val() == 'hp') {        jQuery('.cf_url').hide();        jQuery('#publish').removeClass('disabledLink');    }    else if (jQuery(this).val() == 'np') {        jQuery('.cf_url').hide();        jQuery('#publish').removeClass('disabledLink');    }    else {         jQuery('.cf_url').show();         jQuery('.clickgateinfo').hide();         jQuery('#cf_slug').trigger('keyup');    }});    });</script><link href="<?php echo plugins_url( 'css/font-awesome.css', __FILE__ ); ?>" rel="stylesheet"><link href="<?php echo plugins_url( 'css/admin.css', __FILE__ ); ?>" rel="stylesheet"><div style="display: none;font-size: 15px;line-height: 19px;">    <?php        // check all CF page slugs        $posts = get_posts(array(            'post_type'   => 'clickfunnels',            'post_status' => 'publish',            'fields' => 'ids'            )        );                foreach($posts as $p){            $slug = get_post_meta($p,"cf_slug",true);            if ($slug != $cf_slug) {                echo "<li class='used_slug'>$slug</li>";            }        }        foreach( get_posts('numberposts=-1') as $post ) {           echo "<li class='used_slug'>$post->post_name</li>";        }        foreach( get_pages('numberposts=-1') as $post ) {           echo "<li class='used_slug'>$post->post_name</li>";        }     ?></div><?php    if(class_exists('CF_API')):        $check_api = new CF_API();        if(!count($check_api->get_funnels())):    ?>        <div id="message" class="badAPI error notice" style="width: 733px;padding: 10px 12px;font-weight: bold"><i class="fa fa-times" style="margin-right: 5px;"></i> There are no Funnels in your ClickFunnels account! Head over to <a href="https://app.clickfunnels.com/" target="_blank">ClickFunnels</a> to get started!</div>    <?php endif; ?><?php endif; ?><div id="message" class="badAPI error notice" style="display: none; width: 733px;padding: 10px 12px;font-weight: